SELECT bg.id, bg.name, bg.app_id, bg.platform_id, bg.description, bg.created_by, bg.last_updated_by, bg.created_time, bg.last_updated_time, bg.tenant_id, bg.renter_id, bg.site_id, b.id as block_id, b.label, b.name as block_name, b.framework, b.content, b.assets, b.last_build_info, b.description as block_description, b.tags, b.latest_version, b.latest_history_id, b.screenshot, b.path, b.occupier_by, b.is_official, b.`public`, b.is_default, b.tiny_reserved, b.npm_name, b.i18n, b.platform_id as block_platform_id, b.app_id as block_app_id, b.content_blocks, b.block_group_id, b.created_by as block_created_by, b.last_updated_by as block_last_updated_by, b.created_time as block_created_time, b.last_updated_time as block_last_updated_time, b.tenant_id as block_tenant_id, b.renter_id as block_renter_id, b.site_id as block_site_id FROM t_block_group bg LEFT JOIN r_block_group_block rbg ON rbg.block_group_id = bg.id LEFT JOIN t_block b ON b.id = rbg.block_id
AND b.created_by = #{blockCreatedBy}
AND b.last_build_info is not null and b.content is not null and b.assets is not null
AND bg.created_by = #{groupCreatedBy}

